      {
          uint256 price1 = addonPrice*(_numCycles*(12));
          uint256 price2 = productPrice*(_numCycles*(12));
          price = price1+(price2);
          uint256 discount = (price*(ixxoDiscountOnAnnualPurchase))/(100);
